import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  template: `
    <!--The content below is only a placeholder and can be replaced.-->
    <div style="text-align:center">
      <img width="300" alt="Angular Logo" src="data:image/svg+xml;base64,">
    </div>
    <h2 (click)="onClickMe()">Click me</h2>
    <h2 (click)="onDontClickMe()">Don't click me</h2>
    <ul>
      <li *ngFor="let item of items">
        <p><a target="_blank" rel="noopener" href="https://angular.io/tutorial">{{item}}</a></p>
      </li>
    </ul>
    <router-outlet></router-outlet>
  `,
  styles: []
})
export class AppComponent {
  items = [1, 2, 3];
  onClickMe() {
    for (let i = 0; i < 100; i++) {
      this.items.push(i);
    }
  }
  onDontClickMe() {
    this.items = [1, 2, 3];
  }
}
